import React from "react";
import { Link } from "react-router-dom";
import "./MovieCard.css";

const MovieCard = ({ movie, watchList, setWatchList }) => {
  const isFavourite = watchList.some((item) => item.id === movie.id);

  const toggleFavourite = () => {
    let updatedList;
    if (isFavourite) {
      updatedList = watchList.filter((item) => item.id !== movie.id);
    } else {
      updatedList = [...watchList, movie];
    }

    setWatchList(updatedList);
    localStorage.setItem("watchList", JSON.stringify(updatedList));
  };

  return (
    <div className="col-lg-3 col-md-4 col-sm-6">
      <div className="movie-card">
        <div className="poster">
          <img
            src={movie.poster || "https://via.placeholder.com/300x450?text=No+Image"}
            alt={movie.title}
          />
        </div>
        <div className="movie-info">
          <h5 className="movie-title">{movie.title}</h5>
          <p className="movie-year">{movie.year}</p>
        </div>
        <div className="movie-actions">
          <Link to={`/movie/${movie.id}`} className="btn btn-primary">
            Xem chi tiết
          </Link>
          <button className="btn btn-outline-danger" onClick={toggleFavourite}>
            {isFavourite ? "Bỏ yêu thích" : "Yêu thích"}
          </button>
        </div>
      </div>
    </div>
  );
};

export default MovieCard;
