import { useNavigate, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";
import { CFormInput } from '@coreui/react'

function MovieForm() {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [genres, setGenres] = useState([]);   // Lưu danh sách thể loại từ backend
  const [selectedGenres, setSelectedGenres] = useState([]); // Lưu các thể loại được chọn
  const [description, setDescription] = useState("");
  const [releaseDate, setReleaseDate] = useState("");
  const [video, setVideo] = useState(null); // Trạng thái lưu video

  const { id } = useParams();
  const navigate = useNavigate();
  const API_BASE = "http://localhost/Movie-react/backend/API";

  // Load genres từ backend (API)
  useEffect(() => {
    axios.get(`${API_BASE}/genres.php`)
      .then(res => setGenres(res.data))
      .catch(err => console.error("Lỗi khi lấy danh sách thể loại:", err));
  }, []);

  // Nếu có id thì load thông tin phim
  useEffect(() => {
    if (id) {
      axios.get(`${API_BASE}/get_movie.php?id=${id}`)
        .then(res => {
          const movie = res.data;
          setTitle(movie.title);
          setAuthor(movie.author);
          setDescription(movie.description);
          setReleaseDate(movie.release_date);
          setSelectedGenres(movie.genres_id); // genres_id sẽ là mảng
        })
        .catch(err => console.error("Lỗi khi lấy thông tin phim:", err));
    }
  }, [id]);

  // Hàm xử lý gửi dữ liệu lên backend
  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("title", title);
    formData.append("author", author);
    formData.append("description", description);
    formData.append("release_date", releaseDate);
    selectedGenres.forEach((genre) => {
  formData.append("category[]", genre);
});


    // Nếu có video, append vào FormData
    if (video) {
      formData.append("video", video);
    }

    // Tạo request tùy thuộc vào việc có id hay không (thêm hay cập nhật)
    const request = id
      ? axios.post(`${API_BASE}/update_movie.php?id=${id}`, formData, { headers: { "Content-Type": "multipart/form-data" } })
      : axios.post(`${API_BASE}/add_movie.php`, formData, { headers: { "Content-Type": "multipart/form-data" } });

    request.then(() => navigate("/admin"))
      .catch(err => console.error("Lỗi khi gửi dữ liệu:", err));
  };

  // Hàm xử lý thay đổi thể loại
  const handleGenreChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, option => option.value);
    setSelectedGenres(selectedOptions);
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <label className="block font-medium">Tên phim</label>
        <input 
          value={title} 
          onChange={e => setTitle(e.target.value)} 
          className="border w-full px-2 py-1" 
          required 
        />
        <CFormInput  value={title} onChange={e => setTitle(e.target.value)} type="text" placeholder="title" aria-label="default input example" required />
      </div>

      <div>
        <label className="block font-medium">Tác giả</label>
        <input 
          value={author} 
          onChange={e => setAuthor(e.target.value)} 
          className="border w-full px-2 py-1" 
        />
      </div>

      <div>
        <label className="block font-medium">Thể loại</label>
        <select
          multiple
          value={selectedGenres}
          onChange={handleGenreChange}
          className="border w-full px-2 py-1"
          required
        >
          {genres.map(genre => (
            <option key={genre.id} value={genre.id}>
              {genre.name}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label className="block font-medium">Mô tả</label>
        <textarea 
          value={description} 
          onChange={e => setDescription(e.target.value)} 
          className="border w-full px-2 py-1" 
          rows={4}
        ></textarea>
      </div>

      <div>
        <label className="block font-medium">Ngày xuất bản</label>
        <input 
          type="date" 
          value={releaseDate} 
          onChange={e => setReleaseDate(e.target.value)} 
          className="border w-full px-2 py-1" 
        />
      </div>

      {/* Input file để tải video */}
      <div>
        <label className="block font-medium">Tải video lên</label>
        <input 
          type="file" 
          onChange={e => setVideo(e.target.files[0])} 
          className="border w-full px-2 py-1" 
        />
      </div>

      <button className="bg-green-500 text-white px-4 py-2 rounded" type="submit">
        {id ? "Cập nhật" : "Thêm"}
      </button>
    </form>
  );
}

export default MovieForm;
