import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import './style.css';
import {
  CTable,
  CTableHeaderCell,
  CTableBody,
  CTableHead,
  CTableRow,
  CTableDataCell,
} from "@coreui/react";
import axios from "axios";

function MovieList() {
  const [movies, setMovies] = useState([]);
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState("");
  const navigate = useNavigate();

  const API_BASE = "http://localhost/Movie-react/backend/API";

  useEffect(() => {
    fetchMovies();
    fetchGenres();
  }, [selectedGenre]);

  const fetchMovies = () => {
    const url = selectedGenre
      ? `${API_BASE}/movies.php?genre=${selectedGenre}`
      : `${API_BASE}/movies.php`;

    axios
      .get(url)
      .then((res) => setMovies(res.data))
      .catch((err) => console.error(err));
  };

  const fetchGenres = () => {
    axios
      .get(`${API_BASE}/genres.php`)
      .then((res) => setGenres(res.data))
      .catch((err) => console.error(err));
  };

  const handleDelete = (id) => {
    if (window.confirm("Bạn chắc chắn muốn xoá?")) {
      axios
        .delete(`${API_BASE}/delete_movie.php?id=${id}`)
        .then(() => fetchMovies())
        .catch((err) => console.error(err));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("admin_token");
    navigate("/login");
  };
  const handleAdd = () => {
    localStorage.removeItem("admin_token");
    navigate("/admin/add");
  };

  return (
    <div>
      <div className="action flex justify-between mb-4">
        <button
          onClick={handleAdd}
          className="bg-green-500 text-white px-4 py-2 roundeds"
        >
          ADD FILM
        </button>
        <Link
          to="/admin/add"
          className="bg-blue-500 text-white px-4 py-2 roundeds"
        >
          ADD FILM
        </Link>
        <button
          onClick={handleLogout}
          className="bg-gray-500 text-white px-4 py-2 rounded"
        >
          LOGOUT
        </button>
        <select
          onChange={(e) => setSelectedGenre(e.target.value)}
          className="border p-2"
        >
          <option value="">Chọn thể loại</option>
          {genres.map((genre) => (
            <option key={genre.id} value={genre.id}>
              {genre.name}
            </option>
          ))}
        </select>
      </div>

      

     <CTable
 
  bordered
  hover
  responsive
  align="middle"
  className="table-admin mb-0"
>
  <CTableHead>
    <CTableRow>
      <CTableHeaderCell>#</CTableHeaderCell>
      <CTableHeaderCell>Tên phim</CTableHeaderCell>
      <CTableHeaderCell>Tác giả</CTableHeaderCell>
      <CTableHeaderCell>Mô tả</CTableHeaderCell>
      <CTableHeaderCell>Thể loại</CTableHeaderCell>
      <CTableHeaderCell>Ngày chiếu</CTableHeaderCell>
      <CTableHeaderCell>Chức năng</CTableHeaderCell>
    </CTableRow>
  </CTableHead>
  <CTableBody>
    {movies.map((movie) => (
      <CTableRow key={movie.id}>
        <CTableDataCell>{movie.id}</CTableDataCell>
        <CTableDataCell>{movie.title}</CTableDataCell>
        <CTableDataCell>{movie.author}</CTableDataCell>
        <CTableDataCell>{movie.description}</CTableDataCell>
        <CTableDataCell>
          {movie.genres?.map((genre) => genre.name).join(", ")}
        </CTableDataCell>
        <CTableDataCell>{movie.release_date}</CTableDataCell>
        <CTableDataCell>
          <Link className="text-primary me-2" to={`edit/${movie.id}`}>
            Sửa
          </Link>
          <button
            className="text-danger"
            onClick={() => handleDelete(movie.id)}
          >
            Xóa
          </button>
        </CTableDataCell>
      </CTableRow>
    ))}
  </CTableBody>
</CTable>

    </div>
  );
}

export default MovieList;
