import { useNavigate, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

function MovieForm() {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [genreId, setGenreId] = useState(""); // Thay vì category
  const [genres, setGenres] = useState([]);   // Danh sách thể loại
  const [description, setDescription] = useState("");
  const [releaseDate, setReleaseDate] = useState("");

  const { id } = useParams();
  const navigate = useNavigate();
  const API_BASE = "http://localhost/Movie-react/backend/API";

  // Load genres từ backend
  useEffect(() => {
    axios.get(`${API_BASE}/genres.php`)
      .then(res => setGenres(res.data))
      .catch(err => console.error("Lỗi khi lấy danh sách thể loại:", err));
  }, []);

  // Nếu có id thì load thông tin phim
  useEffect(() => {
    if (id) {
      axios.get(`${API_BASE}/get_movie.php?id=${id}`)
        .then(res => {
          const movie = res.data;
          setTitle(movie.title);
          setAuthor(movie.author);
          setGenreId(movie.genres_id); // đổi sang genres_id
          setDescription(movie.description);
          setReleaseDate(movie.release_date);
        })
        .catch(err => console.error("Lỗi khi lấy thông tin phim:", err));
    }
  }, [id]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const payload = {
      title,
      author,
      genres_id: genreId, // đổi từ category thành genres_id
      description,
      release_date: releaseDate
    };

    const request = id
      ? axios.post(`${API_BASE}/update_movie.php?id=${id}`, payload)
      : axios.post(`${API_BASE}/add_movie.php`, payload);

    request.then(() => navigate("/admin"))
      .catch(err => console.error("Lỗi khi gửi dữ liệu:", err));
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <label className="block font-medium">Tên phim</label>
        <input value={title} onChange={e => setTitle(e.target.value)} className="border w-full px-2 py-1" required />
      </div>

      <div>
        <label className="block font-medium">Tác giả</label>
        <input value={author} onChange={e => setAuthor(e.target.value)} className="border w-full px-2 py-1" />
      </div>

      <div>
        <label className="block font-medium">Thể loại</label>
        <select
          value={genreId}
          onChange={e => setGenreId(e.target.value)}
          className="border w-full px-2 py-1"
          required
        >
          <option value="">-- Chọn thể loại --</option>
          {genres.map(genre => (
            <option key={genre.id} value={genre.id}>
              {genre.name}
            </option>
          ))}
        </select>
      </div>

      <div>
        <label className="block font-medium">Mô tả</label>
        <textarea value={description} onChange={e => setDescription(e.target.value)} className="border w-full px-2 py-1" rows={4}></textarea>
      </div>

      <div>
        <label className="block font-medium">Ngày xuất bản</label>
        <input type="date" value={releaseDate} onChange={e => setReleaseDate(e.target.value)} className="border w-full px-2 py-1" />
      </div>

      <button className="bg-green-500 text-white px-4 py-2 rounded" type="submit">
        {id ? "Cập nhật" : "Thêm"}
      </button>
    </form>
  );
}

export default MovieForm;
