import { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

function MovieList() {
  const [movies, setMovies] = useState([]);
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState("");
  const navigate = useNavigate();

  const API_BASE = "http://localhost/Movie-react/backend/API";

  useEffect(() => {
    fetchMovies();
    fetchGenres();
  }, [selectedGenre]);

  const fetchMovies = () => {
    const url = selectedGenre
      ? `${API_BASE}/movies.php?genre=${selectedGenre}`
      : `${API_BASE}/movies.php`;

    axios
      .get(url)
      .then((res) => setMovies(res.data))
      .catch((err) => console.error(err));
  };

  const fetchGenres = () => {
    axios
      .get(`${API_BASE}/genres.php`)
      .then((res) => setGenres(res.data))
      .catch((err) => console.error(err));
  };

  const handleDelete = (id) => {
    if (window.confirm("Bạn chắc chắn muốn xoá?")) {
      axios
        .delete(`${API_BASE}/delete_movie.php?id=${id}`)
        .then(() => fetchMovies())
        .catch((err) => console.error(err));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("admin_token");
    navigate("/login");
  };

  return (
    <div>
      <div className="flex justify-between mb-4">
        <Link
          to="/admin/add"
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          ADD FILM
        </Link>
        <button
          onClick={handleLogout}
          className="bg-gray-500 text-white px-4 py-2 rounded"
        >
          LOGOUT
        </button>
      </div>

      <div className="mb-4">
        <select
          onChange={(e) => setSelectedGenre(e.target.value)}
          className="border p-2"
        >
          <option value="">Chọn thể loại</option>
          {genres.map((genre) => (
            <option key={genre.id} value={genre.id}>
              {genre.name}
            </option>
          ))}
        </select>
      </div>

      <table className="w-full border">
        <thead>
          <tr>
            <th className="border px-2 py-1">ID</th>
            <th className="border px-2 py-1">Tên phim</th>
            <th className="border px-2 py-1">Ngày chiếu</th>
            <th className="border px-2 py-1">Thể Loại</th>
          </tr>
        </thead>
        <tbody>
          {movies.map((movie) => (
            <tr key={movie.id}>
              <td className="border px-2 py-1">{movie.id}</td>
              <td className="border px-2 py-1">{movie.title}</td>
              <td className="border px-2 py-1">{movie.release_date}</td>
              <td className="border px-2 py-1">
                {movie.genres && movie.genres.map((genre) => (
                  <span key={genre.id} className="mr-2">{genre.name}</span>
                ))}
              </td>
              <td className="border px-2 py-1">
                <Link
                  className="text-blue-600 mr-2"
                  to={`admin/edit/${movie.id}`}
                >
                  Sửa
                </Link>
                <button
                  className="text-red-600"
                  onClick={() => handleDelete(movie.id)}
                >
                  Xóa
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default MovieList;
