import { useNavigate, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

function MovieForm() {
  const [title, setTitle] = useState("");
  const [releaseDate, setReleaseDate] = useState("");
  const [genres, setGenres] = useState([]);
  const [selectedGenres, setSelectedGenres] = useState([]);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    // Lấy danh sách thể loại
    axios.get("http://localhost/src/API/genres.php")
      .then(res => setGenres(res.data))
      .catch(err => console.error(err));

    if (id) {
      // Lấy thông tin phim khi sửa
      axios.get(`http://localhost/src/API/get_movie.php?id=${id}`)
        .then(res => {
          setTitle(res.data.title);
          setReleaseDate(res.data.release_date);
          setSelectedGenres(res.data.genres); // Giả sử API trả về danh sách thể loại của phim
        })
        .catch(err => console.error(err));
    }
  }, [id]);

  const handleGenreChange = (e) => {
    const { value } = e.target;
    setSelectedGenres(prev => {
      if (prev.includes(value)) {
        return prev.filter(genre => genre !== value);
      }
      return [...prev, value];
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const payload = { 
      title, 
      release_date: releaseDate, 
      genres: selectedGenres 
    };

    if (id) {
      axios.post(`http://localhost/src/API/update_movie.php?id=${id}`, payload)
        .then(() => navigate("/"));
    } else {
      axios.post("http://localhost/src/API/add_movie.php", payload)
        .then(() => navigate("/"));
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div>
        <label className="block font-medium">Tên phim</label>
        <input value={title} onChange={e => setTitle(e.target.value)} className="border w-full px-2 py-1" required />
      </div>
      <div>
        <label className="block font-medium">Ngày chiếu</label>
        <input type="date" value={releaseDate} onChange={e => setReleaseDate(e.target.value)} className="border w-full px-2 py-1" required />
      </div>
      <div>
        <label className="block font-medium">Thể loại</label>
        <div className="space-y-2">
          {genres.map(genre => (
            <div key={genre.id}>
              <input 
                type="checkbox" 
                value={genre.id} 
                checked={selectedGenres.includes(genre.id.toString())} 
                onChange={handleGenreChange} 
              />
              <label>{genre.name}</label>
            </div>
          ))}
        </div>
      </div>
      <button className="bg-green-500 text-white px-4 py-2 rounded" type="submit">
        {id ? 'Cập nhật' : 'Thêm'}
      </button>
    </form>
  );
}

export default MovieForm;
