import React from 'react';
import MovieCard from '../MovieCard';
import './style.css';

const TopMovies = ({ filterCtg, setFilterCtg, topMovies = [], genres = [] }) => {
  const handleFilterCtg = (e) => {
    setFilterCtg(e.target.textContent);
  };

  return (
    <TopMovies
      genres={genres}
      filterCtg={filterCtg}
      setFilterCtg={setFilterCtg}
      topMovies={topMovies}
    />
  );
};

export default TopMovies;
