import React from "react";
import { Link } from "react-router-dom";
import { useState, useEffect } from "react";

import "./style.css";

const MovieCard = ({ movie }) => {
  // Phân biệt các kiểu key (OMDb vs dữ liệu từ backend)
  const movieId = movie.imdbID || movie.id;
  const title = movie.Title || movie.title || "No title";

  const posterUrl = movie.poster_url
    ? `http://localhost/Movie/backend/Image/${movie.poster_url}`
    : "/default.jpg"; // Nếu backend không có ảnh
  const video_url = movie.video_url
    ? `http://localhost/Movie/backend/Image/${movie.video_url}`
    : "/default.jpg"; // Nếu backend không có ảnh
  const year =
    movie.Year ||
    (movie.release_date ? movie.release_date.split("-")[0] : "N/A");
  const type =
    Array.isArray(movie.genres) && movie.genres.length > 0
      ? movie.genres.map((g) => g.name).join(", ")
      : "Movie";

  const [watchList, setWatchList] = useState(() => {
  const saved = localStorage.getItem('watchList');
  return saved ? JSON.parse(saved) : [];
});

const watchListIds = watchList.map((item) => item.imdbID);

const addToFavourite = (movie) => {
  if (watchListIds.includes(movie.imdbID)) {
    alert('Movie already added to your watchlist');
  } else {
    setWatchList([...watchList, movie]);
  }
};

const deleteFromWatchList = (id) => {
  const newWatchList = watchList.filter((item) => item.imdbID !== id);
  setWatchList(newWatchList);
};

useEffect(() => {
  localStorage.setItem('watchList', JSON.stringify(watchList));
}, [watchList]);


  return (
    <div className="single-movie">
      <div className="movie-poster">
        <Link className="link" to={`/movie/${movieId}`}>
          <img src={posterUrl} alt="movie-poster" />
        </Link>
        <ul className='overlay-btns'>
          {watchListIds.includes(video_url) ? 
            (<li>
              <button
                className='btn watch-btn'
                onClick={() => deleteFromWatchList(video_url)}>
                Delete From Watch
              </button>
            </li>
            )
            :
            (
            <li>
              <button
                className='btn watch-btn'
                onClick={() => addToFavourite(movie)}>
                Watch Later
              </button>
            </li>
          )}
          <li>
            <Link className='btn details-btn' to={`/movie/${video_url}`}>
              Details
            </Link>
          </li>
        </ul>
      </div>
      <div className="movie-content">
        <div className="top row">
          <h5 className="title">
            <Link className="link" to={`/movie/${movieId}`}>
              {title.length > 20 ? title.slice(0, 20) + "..." : title}
            </Link>
          </h5>
          <h6 className="year">{year}</h6>
        </div>
        <div className="bottom row">
          <span className="quality">HD</span>
          <span className="type">{type}</span>
        </div>
      </div>
    </div>
  );
};

export default MovieCard;
